def relative(sol, opt):
    rel = []
    for i in range(len(sol)):
        rel.append(opt[i]-sol[i])
    return rel
#######################################################################################
def plot1():
    plt.plot(nodes, relative(greedy_sol,eps_05_sol), 'k+', label = "1+Eps, Eps=0.5", markersize='10')
    plt.plot(nodes, relative(greedy_sol, eps_025_sol), 'gx', label = "1+Eps, Eps=0.25", markersize='7')
    plt.plot(nodes, relative(greedy_sol,eps_01_sol), 'r*', label = "1+Eps, Eps=0.1")
    plt.xlabel("Number of Transactions")
    plt.ylabel("Profit relative to the greedy approximation")
    plt.legend(loc='upper right')
    plt.show()
